import React, { Component } from 'react'
import { Menu } from 'antd';
import { Link } from 'react-router-dom'
import { AppstoreOutlined, MailOutlined, SettingOutlined } from '@ant-design/icons';
import './MenuSilder.scss'
import 'antd/dist/antd.css';
import Item from 'antd/lib/list/Item';
const { SubMenu } = Menu;
export default class MenuSilder extends Component {
  constructor() {
    this.state = {
      menus: [
        {
          name: 'Domitory 1', link: '/Domitory1',
          submenu: [
            { name: "Student Management", link: "/Domotory1/Student" },
            { name: "Room Management", link: "/Domotory1/Room" }
          ]
        }, {
          name: 'Domitory 2', link: '/Domitory1',
          submenu: [
            { name: "Student Management", link: "/Domotory1/Student" },
            { name: "Room Management", link: "/Domotory1/Room" }
          ]
        }, {
          name: 'Domitory 3', link: '/Domitory1',
          submenu: [
            { name: "Student Management", link: "/Domotory1/Student" },
            { name: "Room Management", link: "/Domotory1/Room" }
          ]
        }
      ]
    }
  }
  handleClick = e => {
    console.log('click ', e);
  };


  render() {
    return (

      <Menu
        onClick={this.handleClick}
        id='LeftMenu'
        defaultSelectedKeys={['1']}
        defaultOpenKeys={['sub1']}
        mode="inline"
      > {
            this.state.menus.map((item,index)=>{
              return <SubMenu className={'dor'+index} key={"sub"+index+1}  title={Item.name}>
                item.submenu?{item.submenu.map((i,inde)=>{
                  return <Menu.Item className='items' key="2">i.name</Menu.Item>
                })}      

              
        </SubMenu>
        })
        }
       
          {/* <SubMenu className='dor one' key="sub1" title="Domitory 1">

